dic={'name':'wjx','age':18}         # 迭代器是一个对象，可以一个一个的访问对象x = iter(dic)                         # 字典是可迭代对象，x为迭代器，y为迭代器，两种创建的方法y = dic.__iter__()print(next(x))print(next(x))print(next(y))print(next(y))# def f1():                              # 生成器是一个函数，里面使用了yield，这个函数也满足了迭代器的条件，所以它也是一个迭代器#     print('begin:')#     while 1:##         food = yield 1                 # 可以通过send函数传值，1相当于没用，把值传给food#         print('food:', end='')#         print(food)## g=f1()# next(g)# next(g)# next(g)# next(g)## g.send('apple')                      # send函数中也运行了next# g.send('banana')# g.send('water')###print('/////////////')# def func1():#     print('begin')#     i=0#     while 1:#         i+=1#         food=yield i#         print(food)## func1()                   # 身为生成器的函数，只能通过next（）来运行# func=func1()# next(func)                # 运行到第一个yield为止，# func.send('1')              # next之前用send会报错# func.send('3')# func.send('2')## ##def func():                              # 相当于自己写了一个迭代器    x = 1    while True:        x += 1        if x == 21:            return None        yield xfor i in func():    print(i)func()